css_code = """
body {
  line-height: 2.4;  
  max-width: 100%;
  font-size: 1.05rem;  
  margin: auto;  
  padding: 1.5rem;
}

img {
  max-width: 100%;
  height: auto;
  display: block;
  margin: 1em auto;
}
"""

with open("styles.css", "w", encoding="utf-8") as f:
    f.write(css_code)

print("✅ styles.css 파일 생성 완료")

